    string public constant ERC7201_NAMESPACE = "SlotDerivationTest.Namespace";
    bytes32 private immutable _ADDRESS_SLOT;
    constructor () {
        _ADDRESS_SLOT = SlotDerivation.erc7201Slot(ERC7201_NAMESPACE);
    }
}

